mapper.select:
select

  distinct


from tb_novel  WHERE updatetime > SUBDATE(NOW(),INTERVAL 1 HOUR);

select 

from tb_novel
where id = #{id,jdbcType=BIGINT}
  and netid = #{netid,jdbcType=BIGINT}

